<template>
    <div class="col-sm-6 offset-sm-3">
        <h2>Login</h2>
        <form @submit.prevent="handleSubmit">
            <div class="form-group">
                <label for="username">Username</label>
                <input type="text" v-model="username" name="username" class="form-control" :class="{ 'is-invalid': submitted && !username }" />
                <div v-show="submitted && !username" class="invalid-feedback">Username is required</div>
            </div>
            <div class="form-group">
                <label htmlFor="password">Password</label>
                <input type="password" v-model="password" name="password" class="form-control" :class="{ 'is-invalid': submitted && !password }" />
                <div v-show="submitted && !password" class="invalid-feedback">Password is required</div>
            </div>
            <div class="form-group">
                <button @click="handleSubmit" class="btn btn-primary" :disabled="loggingIn">Login</button>
                <img v-show="loggingIn" src="data:image/gif;base64," />
                <router-link to="/register" class="btn btn-link">Register</router-link>
            </div>
        </form>
    </div>
</template>

<script>

export default {
    data () {
        return {
            username: '',
            password: '',
            submitted: false,
            loggingIn: false
        }
    },
    computed: {

    },
    created () {
        // reset login status
        this.logout();
    },
    methods: {

        handleSubmit () {
            this.submitted = true;
            const { username, password } = this;
            if (username && password) {
                this.login( this.username, this.password )
            }
        },
        login (username, password) {
            var path = 'http://localhost:8080';
            localStorage.setItem('path', path);
            this.loggingIn = true;
            var vm = this;
            var json = { 'userEmail': username, 'password': password };
            vm.$http.post(localStorage.getItem('path') + '/login', json).then(function(response) {
                console.log(response.body);
                var obj = { email: username, token: response.body.accessToken };
                localStorage.setItem('user', JSON.stringify(obj));
                var user = JSON.parse(localStorage.getItem('user')); //
                console.log(user); //
                this.$router.push('/');
            });
        },
        logout () {
            localStorage.removeItem('user');
        }
    }
};
</script>
